mapper.update:
update notice
set title = #{title}, content = #{content}
where no = #{no}

	select no
	from notice
	where no = #{no}

mapper.select:
select no, title, author, regDate, hit, content, type
from notice
where no = #{no}

mapper.update:
update notice
set hit = hit + 1
where no = #{no}

mapper.select:
select no, title, author, regDate, content, hit, type
from notice


		type = #{type}


		${key} like concat('%', #{keyword}, '%') 


order by regDate desc, no asc
limit #{start}, #{amount}
