mapper.select:
select id, pass, email, name, phone, age, gender
from user
where id = #{id} and pass = #{pass}

mapper.update:
update user
set pass = #{pass}, email = #{email}, name = #{name}, phone = #{phone}
where id = #{id}

update user
set pass = #{pass}
where id = #{id} and email = #{email} and name = #{name}

